mapper.insert:
INSERT INTO user (
username, user_pw, nickname, user_img, email, phone,
breg_num, company_name, master_name, company_addr
) VALUES (
#{username}, #{user_pw}, #{nickname}, #{user_img}, #{email}, #{phone},
#{breg_num}, #{company_name}, #{master_name}, #{company_addr}
)

mapper.select:
SELECT username, user_pw
FROM user
WHERE username = #{username}
